// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import AdminDashboard from './components/AdminDashboard';
import UserDashboard from './components/UserDashboard';

function App() {
  const [userRole, setUserRole] = useState(null); // Stores the logged-in user's role

  const login = (role) => {
    setUserRole(role); // Set the role based on login
  };

  const logout = () => {
    setUserRole(null); // Reset role on logout
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Login onLogin={login} />} />
        <Route
          path="/admin"
          element={userRole === 'admin' ? <AdminDashboard onLogout={logout} /> : <Navigate to="/" />}
        />
        <Route
          path="/user"
          element={userRole === 'user' ? <UserDashboard onLogout={logout} /> : <Navigate to="/" />}
        />
      </Routes>
    </Router>
  );
}

export default App;
